class COLORS:
    """Color codes for terminal colors
    """
    RED = '\033[31m'
    ALT_RED = '\033[91m'
    BRIGHT_RED = '\033[1;31m'
    ALT_BRIGHT_RED = '\033[1;91m'
    YELLOW = '\033[33m'
    BRIGHT_YELLOW = '\033[1;33m'
    BLUE = '\033[34m'
    BRIGHT_BLUE = '\033[1;34m'
    ALT_BLUE = '\033[94m'
    ALT_BRIGHT_BLUE = '\033[1;94m'
    BOLD = '\033[1m'
    UNDERLINE = '\033[4m'
    ENDC = '\033[0m'
